CREATE DATABASE IF NOT EXISTS `fitness_java` DEFAULT CHARACTER SET 'utf8';
USE `fitness_java`;
CREATE TABLE IF NOT EXISTS `fitness_java`.`trainer`(id Int UNSIGNED NOT NULL AUTO_INCREMENT, full_name Varchar(255) NOT NULL, birth_date Date NOT NULL, passp_no Varchar(255) NOT NULL, authority Varchar(255) NOT NULL, issue_date Date NOT NULL, sex ENUM('М', 'Ж', 'Мужской', 'Женский', 'M', 'F', 'Male', 'Female') NOT NULL,CONSTRAINT chk_passp_date CHECK (birth_date < passp_date), PRIMARY KEY(id));
